$(document).ready(() => { $("#CurrencyName").change(() => { let e = $("#CurrencyName").val(); $.ajax({ type: "GET", dataType: "json", data: { currencyName: e }, url: "/Wallet/GetAvailableAmount" }).done(e => { $("#AvailableAmount").text(e), "0,00" == e ? $("#Create input[type='submit']").prop("disabled", !0) : $("#Create input[type='submit']").prop("disabled", !1) }) }) });